package JavaTraining;

public class CharConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char c1 = 'a';
		char d1 = 'A';
		System.out.println(c1);
		System.out.println(c1+d1);
		// every character in Java has a numeric value which is called ASCII Code
		
		char num1 = '1';
		char num2 = '2';
		System.out.println(num1); // displays the char only
		System.out.println(num1+num2); // sums up the ASCII Code values
}

}
