package JavaTraining;

public class Feb1Practice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Variable (changeable)
		// + Addition
		// - Subtraction
		// * Multiplication
		// / Division
		// % Modulus - Remainder
		// ++ Increment
		// -- Decrement
		System.out.println(9 + 8);
		int num1 = 9;

		System.out.println(num1);

		int num2 = 90;

		System.out.println(num1 + num2);

		System.out.println(num1 + 19);
		num2--;

		System.out.println("This is num2 : " + num2);
		System.out.println(num2 % num1);
		System.out.println("If i divide 10 by 3, i will get remainder as : " + (10 % 3));
		System.out.println("If i divide 10 by 3, i will get answer as : " + (10 / 3));

		// Assigning operators
		int age = 50;
		System.out.println(age);
		age = num1;
		System.out.println(age);

		// Decrement operator
		int num = 100;
		System.out.println("my number : " + num);
		num--;
		// now my num = 99
		System.out.println("my number : " + num);
		num--;
		// now my num = 98
		System.out.println("my number : " + num);
		num--;
		// now my num = 97
		System.out.println("my number : " + num);
		num++;
		// now my num = 98
		System.out.println("my number : " + num);

		int age1 = 9;

		double age3 = age1;
		System.out.println(age3);

	}

}
